mapper.select:
select ta.id, ta.name, tu.name as owner, ta.startDate, ta.endDate
from tbl_activity as ta
join tbl_user as tu
on ta.owner = tu.id


        ta.name like "%"#{name}"%"


        and tu.name like "%"#{owner}"%"


        and ta.startDate > #{startDate}


        and ta.endDate < #{endDate}


order by ta.createTime desc
limit #{skipCount}, #{pageSize}

select count(*) from tbl_activity as ta join tbl_user as tu on ta.owner = tu.id
ta.name like "%"#{name}"%"
and tu.name like "%"#{owner}"%"
and ta.startDate > #{startDate}
and ta.endDate < #{endDate}

